import React from "react";
import "./Form.css";
const Forms = () => {
    const [values, setValues] = React.useState({
        email: "testclient@gmail.com",
        password: "1234",
        showPassword: false
    });

    const handleChange = prop => event => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({ ...values, showPassword: !values.showPassword });
    };
    const handleSubmit = e => {
        e.preventDefault();
        if (!values.email || !values.password) {
            alert("Please fill out the form");
            return;
        }
        // Post request to backend
        fetch(`https://click.prographer.com/api/adminUser/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                email: values.email,
                password: values.password
            })
        })
            .then(res => res.json())
            .then(json => {
                if (json.success) {
                    console.log("this route was successfull");
                    setValues({
                        ...values,
                        password: "",
                        email: ""
                    });
                } else {
                    console.log("this route did not go through");
                }
            });
    };
    return (
        <div class="card bg-primary text-center card-from py-5 m-auto">
            <div class="card-body">
                <h3>Sign Up</h3>
                <p>Please fill out this form to register</p>
                <form>
                    <div class="form-group my-5">
                        <input
                            type="text"
                            class="from-control form-control-lg"
                            placeholder="Username"
                        />
                    </div>
                    <div class="form-group my-5">
                        <input
                            type="email"
                            class="from-control form-control-lg"
                        />
                    </div>
                    <div class="form-group my-5">
                        <input
                            type="password"
                            class="from-control form-control-lg"
                        />
                    </div>
                    <div class="form-group my-5">
                        <input
                            type="password"
                            class="from-control form-control-lg"
                        />
                    </div>
                    <input
                        type="submit"
                        value="Submit"
                        class="btn btn-outline btn-block btn-danger my-5"
                    />
                </form>
            </div>
        </div>
    );
};

export default Forms;
