import React, { Component } from "react";
import questions from "../data/questions";
export default class Quiz extends Component {
    state = {
        questionCout: 0,
        questions: [...questions],
        finished: false,
        answeredQuestion: [],
        totalMarks: 0
    };
    prev() {
        const { questionCout, questions, answeredQuestion } = this.state;
        if (
            questions[questionCout].answerCorrect &&
            questions[questionCout].answerCorrect ===
                answeredQuestion[questionCout]
        ) {
            console.log("answered question  ", answeredQuestion[questionCout]);
        }
        if (questionCout > 0) {
            this.setState({
                questionCout: this.state.questionCout - 1,
                finished: false
            });
        } else {
            this.setState({
                questionCout: 0,
                finished: true
            });
        }
    }
    click(given, answer) {
        const { questionCout } = this.state;
        var joined = this.state.answeredQuestion.concat(answer);
        this.setState({ answeredQuestion: joined });
        if (given === answer) {
            this.setState({
                totalMarks: this.state.totalMarks + 1
            });
        }
        if (questionCout < 1) {
            console.log(answer, questionCout, "questionCout<1");
            this.setState({
                questionCout: questionCout + 1
            });
        } else {
            console.log(answer, questionCout, "else");
            this.setState({
                finished: true
            });
        }
        console.log("questionCout questionCout=======", this.state.i);
    }
    render() {
        const { questionCout, questions, finished, totalMarks } = this.state;
        var answered = true;
        const prevButton = (
            <button
                className="btn col"
                onClick={() => {
                    this.prev(true);
                }}
            >
                <p className="lead">prev</p>
            </button>
        );

        if (finished)
            return (
                <h1 className="text-center m-5 text-primary">
                    {prevButton}
                    Quiz is finished <br />
                    Your answer being {totalMarks}
                </h1>
            );
        if (questionCout < 0) {
            console.log('the cou');
            return (
                <h1 className="text-center m-5 text-primary">
                    {prevButton}
                    Lets start the quiz
                    <button className="btn btn-primary border">NOW</button>
                </h1>
            );
        }

        return (
            <div className="container">
                <div className="col">
                    <div className="container">
                        <h2 className="bg-primary text-black text-capitalize m-auto">
                            {prevButton}
                            {questions[questionCout].questionno}
                        </h2>
                        <div className="bg-success row">
                            {questions[questionCout].options.map(value => {
                                return (
                                    <button
                                        className="btn col"
                                        key={value.key}
                                        disabled={answered ? false : true}
                                        onClick={() => {
                                            this.click(
                                                value.key,
                                                questions[questionCout]
                                                    .answerCorrect
                                            );
                                        }}
                                    >
                                        <p className="lead">{value.ques}</p>
                                    </button>
                                );
                            })}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
