import React, { Component } from "react";
import questions from "../data/questions";
export default class Quiz extends Component {
    state = {
        qCount: 0,
        questions: [...questions],
        finished: false,
        answeredQuestion: [],
        totalMarks: 0
    };
    prev() {
        const { qCount, questions, answeredQuestion } = this.state;
        if (
            questions[qCount].ansCorrect &&
            questions[qCount].ansCorrect === answeredQuestion[qCount]
        ) {
            console.log("answered question  ", answeredQuestion[qCount]);
        }
        if (qCount > 0) {
            this.setState({
                qCount: this.state.qCount - 1,
                finished: false
            });
        }
        if (qCount === 0) {
            this.setState({
                qCount: 0,
                finished: true
            });
        }
    }
    click(given, answer) {
        const { qCount } = this.state;
        var joined = this.state.answeredQuestion.concat(answer);
        this.setState({ answeredQuestion: joined }); //to add ht e list of questions answered
        if (given === answer) {
            this.setState({
                totalMarks: this.state.totalMarks + 1
            });
        }
        if (qCount === 0) {
            console.log(answer, qCount, "qCount<1");
            this.setState({
                qCount: qCount + 1
            });
        } else {
            console.log(answer, qCount, "else");
            this.setState({
                finished: true
            });
        }
        console.log("qCount qCount=======", this.state.i);
    }
    render() {
        const { qCount, questions, finished, totalMarks } = this.state;
        var answered = true;

        if (finished)
            return (
                <h1 className="text-center m-5 text-primary">
                    <button className="btn btn-success m-auto p-auto">
                        <a href="/">Home</a>
                    </button>
                    <br />
                    Quiz is finished <br />
                    Your answer being {totalMarks}
                </h1>
            );
        return (
            <div className="container">
                <div className="col">
                    <div className="container">
                        <div className="text-black text-capitalize text-center">
                            <button
                                className="btn col-3 btn-danger"
                                onClick={() => {
                                    this.prev(true);
                                }}
                            >
                                Prev
                            </button>
                            <h2 className="text-center m-3">
                                {questions[qCount].questionno}
                            </h2>
                        </div>
                        <div className="row">
                            {questions[qCount].options.map(value => {
                                return (
                                    <button
                                        className="btn col btn-light m-1"
                                        key={value.key}
                                        disabled={answered ? false : true}
                                        onClick={() => {
                                            this.click(
                                                value.key,
                                                questions[qCount].ansCorrect
                                            );
                                        }}
                                    >
                                        <p className="lead text-capitalize p-auto text-dark mt-2 text-center">
                                            <b>{value.ques}</b>
                                        </p>
                                    </button>
                                );
                            })}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
